import AchievementData from '@/data/AchievementData.ts'

export default [
    new AchievementData("achievements-1", "Game Systems Showcase", "img/achievements/icon.png", `
    <div class="paragraph">
      <strong>Notable Achievements</strong> highlighting key technical implementations and creative solutions in my game development projects.
    </div>

    <div class="achievement-section">
      <div class="video-container">
        <video autoplay loop muted playsinline width="100%">
          <source src="video/wrapping.mp4" type="video/mp4">
          Your browser does not support the video tag.
        </video>
      </div>
      <div class="achievement-info">
        <h3>Dynamic Wrapping System</h3>
        <p>Implemented real-time mesh deformation with OBI Cloth physics to create a realistic gift wrapping mechanic, solving challenges with collision detection and material stretching.</p>
        <div class="tech-tags">
          <span>Unity</span>
          <span>OBI Cloth</span>
          <span>Shader Graph</span>
        </div>
      </div>
    </div>

    <div class="divider"></div>

    <div class="achievement-section">
      <div class="video-container">
        <video autoplay loop muted playsinline width="100%">
          <source src="video/Plant shooter.mp4" type="video/mp4">
          Your browser does not support the video tag.
        </video>
      </div>
      <div class="achievement-info">
        <h3>Procedural Growth System</h3>
        <p>Developed a plant-based shooter using Ivy Studio's procedural generation, creating dynamic vine growth patterns that respond to player input and environment.</p>
        <div class="tech-tags">
          <span>Ivy Studio</span>
          <span>Procedural Generation</span>
          <span>Unity ECS</span>
        </div>
      </div>
    </div>

    <div class="divider"></div>

    <div class="achievement-section">
      <div class="video-container">
        <video autoplay loop muted playsinline width="100%">
          <source src="video/WallRunStart.mp4" type="video/mp4">
          Your browser does not support the video tag.
        </video>
      </div>
      <div class="achievement-info">
        <h3>Path-Based Movement</h3>
        <p>Created a line-renderer navigation system where players draw paths for AI characters to follow, with triggered actions at specific waypoints.</p>
        <div class="tech-tags">
          <span>Line Renderer</span>
          <span>AI Pathfinding</span>
          <span>Unity</span>
        </div>
      </div>
    </div>

    <style>
      .achievement-section {
        display: flex;
        gap: 20px;
        margin: 25px 0;
        align-items: center;
      }
      
      .video-container {
        width: 270px;
        height: 280px;
        flex-shrink: 0;
      }
      
      .video-container video {
        width: 100%;
        height: 100%;
        object-fit: cover;
        border-radius: 8px;
        background-color: #000;
      }
      
      .achievement-info {
        flex-grow: 1;
      }
      
      .achievement-info h3 {
        font-size: 20px;
        margin-bottom: 10px;
        color: #333;
      }
      
      .achievement-info p {
        font-size: 14px;
        line-height: 1.5;
        color: #666;
        margin-bottom: 15px;
      }
      
      .tech-tags {
        display: flex;
        gap: 8px;
        flex-wrap: wrap;
      }
      
      .tech-tags span {
        background-color: #f0f0f0;
        padding: 4px 10px;
        border-radius: 12px;
        font-size: 12px;
        color: #555;
      }
      
      .divider {
        height: 1px;
        background-color: #dcdcdc;
        margin: 20px 0;
        width: 100%;
      }
      
      @media (max-width: 768px) {
        .achievement-section {
          flex-direction: column;
        }
        
        .video-container {
          width: 100%;
          height: auto;
          aspect-ratio: 0.96;
        }
      }
    </style>
    `, "#4285f4", false, true)
]
